pragma solidity ^0.4.2;
import "./DappToken.sol";

contract AuctionBid {
    address admin;
   
    
    address public highestBidder;
    uint public highestPrice;
    bool ended;
    bool started;
    address public owner;
    uint public nextStep;
    mapping(address => uint) withdrawMoney;
    struct Bid {
        address bidder;
        uint value;
        uint time;
    }
    Bid[] public bids;
    DappToken public blc;
    uint public balanceOf;
    
    event HighestBidIncrease(address player, uint value, uint time);
    event AuctionEnded(address winner, uint value);
    
    constructor() public {
        admin = 0xcB000A6BD06Efed410B8438100C46C98f685c616;
        blc = DappToken(0x0EF72c129Aed97E869bDDEdFB4a3EA628e9a7eb4);
        
    }
    function startBidding(uint _startPrice) public {
        require(started == false, "Auction started");
        require(ended == false, "Auction ended");
        owner = msg.sender;
        started = true;
        highestPrice = _startPrice;
        nextStep = (highestPrice + (highestPrice*50)/100);
    }
    
    function bid(uint _time, uint _value) public {
        require(ended == false, "Auction ended");
        require(started == true, "Auction is closed");
    
        require(blc.balanceOf(msg.sender) >= nextStep, "You have no money, let buy some");
        require(_value >= nextStep, "Can't bid under the next price");

        blc.transferFrom(msg.sender,this,_value);
           
        bids.push(Bid(msg.sender, _value, _time));
        highestBidder = msg.sender;
        highestPrice = _value;
        withdrawMoney[highestBidder] += highestPrice;
        emit HighestBidIncrease(msg.sender, _value, _time);
        nextStep = highestPrice + (highestPrice*50)/100;
    }

    function auctionEnd() public {
        // require(msg.sender == admin, "Only the admin can do this");
        require(ended == false, "Auction not yet ended");
        ended = true;
        started = false;
        emit AuctionEnded (highestBidder, highestPrice);
        uint fees = (highestPrice * 10)/100;
        highestPrice -= fees;
       
        blc.transfer(admin, fees);
        blc.transfer(owner, highestPrice);
    }
    function withdraw() public  {
        require(msg.sender != highestBidder, "Can only withdraw money when auction ended");
        
        uint amount = withdrawMoney[msg.sender];
        if(amount > 0 ) {
            withdrawMoney[msg.sender] = 0;
            blc.transfer(msg.sender, amount);
        }
    }
    function getBidLength () public view returns(uint) {
        return bids.length;
    }
}
